import "../styles/Pages/share.css";
import { PermMedia, Label, Room, EmojiEmotions } from "@material-ui/icons"
import { getId } from './../configstore';
import { Users } from "../utils/data";
import axios from "axios";
import { useEffect, useState } from "react";

export function Share() {
    let url = 'http://127.0.0.1:5789/people/';
    let url1 = 'http://127.0.0.1:5789/post';
    const Id = getId().id;
    url = url + getId().id;
    const [user, setuser] = useState('')
    const [desc, setDesc] = useState('')

    useEffect(() => {
        axios.get(url)
            .then(response => {
                console.log(response.data);

                setuser(response.data.nickname);
            });
    }, [])


    const post = {
        userId: Id,
        desc: desc,
        img: "",
        likes: [],
        Comment: [],
        category: "props(my child)"
    }


    function addPost() {


        axios.post(url1, {
            post
        })
            .then(response => {
                console.log(response.data);

            });

    }


    return (
        <div className="sharearoundAll">
            <div className="share">
                <div className="shareWrapper">
                    <div className="shareTop">
                        <img className="shareProfileImg" src="./images/anonymous.jpg" alt="" />
                        <input onChange={event => setDesc(event.target.value)}
                            placeholder={"Whats in your mind" + " " + user + "?"}
                            className="shareInput"

                        />
                    </div>
                    <hr className="shareHr" />
                    <div className="shareBottom">
                        <div className="shareOptions">
                            <div className="shareOption">
                                <PermMedia htmlColor="tomato" className="shareIcon" />
                                <span className="shareOptionText">Photo or Video</span>
                            </div>
                            <div className="shareOption">
                                <Label htmlColor="blue" className="shareIcon" />
                                <span className="shareOptionText">Tag</span>
                            </div>
                            <div className="shareOption">
                                <Room htmlColor="green" className="shareIcon" />
                                <span className="shareOptionText">Location</span>
                            </div>
                            <div className="shareOption">
                                <EmojiEmotions htmlColor="goldenrod" className="shareIcon" />
                                <span className="shareOptionText">Feelings</span>
                            </div>
                        </div>
                        <button onClick={addPost} className="shareButton">Share</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
