import { Outlet } from 'react-router-dom'
import '../styles/Pages/asking.css'
import '../styles/Pages/post.css'
import { AskIcons, AskNav } from './../components/navbar';
import { askNavArr, AskNAvIcons, Posts } from './../utils/data';
import { MoreVert } from "@material-ui/icons";
import { Users } from "./../utils/data";
import { useState } from "react";


export function Post(props: { items: { id: number, desc: string, date: string, userId: number, like: number, comment: number } }) {

    const [like, setLike] = useState(props.items.like)
    const [isLiked, setIsLiked] = useState(false)

    const likeHandler = () => {
        setLike(isLiked ? like - 1 : like + 1)
        setIsLiked(!isLiked)
    }
    return (
        <div className="post">
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTopLeft">
                        <img
                            className="postProfileImg"
                            src={Users.filter((u) => u.id === props.items?.userId)[0].profilePicture}
                            alt=""
                        />
                        <span className="postUsername">
                            {Users.filter((u) => u.id === props.items?.userId)[0].username}
                        </span>
                        <span className="postDate">{props.items.date}</span>
                    </div>
                    <div className="postTopRight">
                        <MoreVert />
                    </div>
                </div>
                <div className="postCenter">
                    <span className="postText">{props.items?.desc}</span>

                </div>
                <div className="postBottom">
                    <div className="postBottomLeft">
                        <img className="likeIcon" src="public/images/like.png" onClick={likeHandler} alt="" />
                        <img className="likeIcon" src="public/images/heart.png" onClick={likeHandler} alt="" />
                        <span className="postLikeCounter">{like} people like it</span>
                    </div>
                    <div className="postBottomRight">
                        <span className="postCommentText">{props.items.comment} comments</span>
                    </div>
                </div>
            </div>

        </div>
    );
}

export function Asking() {

    const [posts, setPosts] = useState([]);
    setPosts(Posts)
    return (

        <header className='askPage'>
            <div className='Ask'>
                <div className='divAroundNavs' >
                    <AskNav navItems={askNavArr} />
                    <AskIcons navItems={AskNAvIcons} />
                    {posts.map((posts) => (
                        <Post items={posts} />
                    ))}
                </div>
                <Outlet />

            </div>

        </header >

    )

}
