import axios from 'axios';
import { useEffect, useState } from 'react';
import { setId } from '../configstore';
import '../styles/Components/signup.css';
import { getId } from './../configstore';
import { useNavigate } from 'react-router-dom';


/* Function that returns an HTML form for signing up to web */
export function Signup() {
    let Navigate = useNavigate()
    const url = 'http://127.0.0.1:5789/people';
    var nextId = "";
    useEffect(() => {
        axios.get(url)
            .then(response => {
                nextId = JSON.stringify(response.data.length + 1);
                setId(nextId);
                console.log(response.data);
            });
    }, []);

    function mySubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        var formDatasignup = new FormData(e.target as HTMLFormElement);
        const user = {
            id: getId().id,
            nickname: formDatasignup.get('nickname'),
            username: formDatasignup.get('username'),
            password: formDatasignup.get('password'),
        }
        console.log(formDatasignup);

        axios.get(url)
            .then(response => {
                let users = response.data;
                for (let val of users) {
                    console.log(val.nickname);
                    console.log(user);
                    if ((val.nickname === user.nickname) || (val.username === user.username)) {

                        (document.querySelector(".validNick") as HTMLElement).innerHTML = 'invalid nickname or user name ';
                        return;
                    }

                }

                axios.post(url, {
                    user
                })
                    .then(res => {
                        console.log(res);
                        console.log(res.data);
                        Navigate('/Home');

                    })

            })
    }

    return (
        <div className='DivAroundSignUpForm'>
            <form onSubmit={(e) => { mySubmit(e) }} >
                <div id="DivAroundFormElements">
                    <div id="nickname">
                        <input type="text" id="nickname" name="nickname" placeholder=" Choose a nick name" />
                    </div>
                    <label className='validNick'></label>
                    <br />

                    <input type="text" id="username" name="username" placeholder="@gmail.com" />
                    <label className='validUser'></label>
                    <br />

                    <input type="password" id="password" name="password" placeholder="Create a password" />
                    <br />

                    <input type="password" id="conpassword" name="conpassword" placeholder='Confirm your password' />
                    <br />

                    <input type="submit" value="Submit" id="Submit" />

                </div>
            </form>
        </div>
    )

}
