import axios from 'axios';
import '../styles/Components/login.css'
// import '../users'
import { useNavigate } from 'react-router-dom';

/* Function that returns an HTML form for logging in to web */
export function Login() {

    const navigate = useNavigate();
    const handleLogin = (e: React.FormEvent<HTMLFormElement>) => {
        // e.preventDefault();
        const url = 'http://127.0.0.1:5789/people';
        var formData = new FormData(e.target as HTMLFormElement);

        axios.get(url)
            .then(response => {
                let users = response.data;

                for (let val of users) {
                    if (val.username === formData.get('username')) {

                        if (formData.get('password') === val.password) {
                            let ID = val.id;
                            (document.querySelector(".passwordCorrection") as HTMLElement).innerHTML = 'Logged in Successfully';

                            navigate('/Ask');

                            return true
                        } else {

                            (document.querySelector(".passwordCorrection") as HTMLElement).innerHTML =
                                'Incorrect username or password';
                        }
                    }
                    (document.querySelector(".passwordCorrection") as HTMLElement).innerHTML = 'Incorrect username or password';
                }


            })
    };
    return (
        <div className='Login'>
            <form onSubmit={(e) => { handleLogin(e) }}>
                <label> user name</label><br />
                <input type="text" id="username" name="username" placeholder="@gmail.com" />
                <br />
                <label > password</label><br />
                <input type="password" id="password" name="password" />
                <p className="passwordCorrection"> </p>
                <br />
                <input type="submit" value="Submit" id="Submit" />
            </form>

        </div>
    )
}
