import { useEffect, useState } from "react"
import { MoreVert } from "@material-ui/icons";
import '../styles/Pages/post.css'
import axios from "axios";
import { Star } from "@material-ui/icons";
import { getId } from "../configstore";

export function Post(props: { postItems: { postId: string, userId: string, desc: string, like: Array<any>, comments: Array<any>, date: string, img: string, Askpro: Boolean } }) {

    // const [like, setLike] = useState(props.postItems.like.length);
    // const [isLiked, setIsLiked] = useState(false);
    const [isOpen, setisOpen] = useState(false);
    const [isopen, setisopen] = useState(false);
    let [fetching, setFetch] = useState(true);
    let Id = props.postItems.userId;
    let url = 'http://127.0.0.1:5789/people/' + Id;
    let url1 = 'http://127.0.0.1:5789/post/' + props.postItems.postId;

    const [user, setuser] = useState({
        nickname: "",
        proCategory: "",
    })


    useEffect(() => {
        console.log(props);

        axios.get(url)
            .then(response => {
                console.log("heeereeeee");

                console.log(url);

                console.log(response.data);
                setuser(response.data)
                setFetch(false)
            });
    }, [])


    const likeHandler = () => {
        // setLike(isLiked ? like + 1 : like - 1)
        // setIsLiked(!isLiked)
    }

    const handleUpdate = () => {

    }

    const handleDelete = () => {

        axios.delete(url1, {
            data: {
                userId: Id
            }
        })
            .then(response => {
                console.log(response.data);

            });
    }


    return fetching ? (<>loading</>) :
        (
            <div className="aroundAll">
                <div className="post">
                    <div className="postWrapper">
                        <div className="postTop">
                            <div className="postTopLeft">
                                <img
                                    className="postProfileImg"
                                    src="./images/anonymous.jpg"
                                    alt=""
                                />
                                <span className="postUsername">
                                    {user.nickname}

                                </span>
                                <span className="postDate">{props.postItems?.date}</span>
                            </div>
                            <div className="postTopRight">
                                {props.postItems.Askpro &&
                                    <Star htmlColor="green" />
                                }
                                <MoreVert onClick={() => { setisopen(isopen ? false : true) }} />
                                {isopen &&
                                    <div>
                                        <ul>
                                            <li onClick={() => { handleUpdate() }}> edit </li>
                                            <li onClick={() => { handleDelete() }}>delete </li>
                                        </ul>
                                    </div>
                                }

                            </div>
                        </div>
                        <div className="postCenter">
                            <img className="postImg" src={props.postItems.img} />
                            {/* <input type="text" defaultValue={props.postItems.desc} /> */}
                            <p> {props.postItems.desc} </p>

                        </div>
                        <div className="postBottom">
                            <div className="postBottomLeft">
                                <img className="likeIcon" src="./images/like.png" onClick={likeHandler} alt="" />
                                <img className="likeIcon" src="./images/heart.png" onClick={likeHandler} alt="" />
                                {/* <span className="postLikeCounter">{like} people like it</span> */}
                            </div>
                            {(user.proCategory !== "" && props.postItems.Askpro || user.proCategory === "" && !props.postItems.Askpro) &&
                                <div className="postBottomRight">
                                    <span onClick={() => { setisOpen(isOpen ? false : true) }} className="postCommentText">{props.postItems.comments} comments</span>
                                </div>
                            }
                            {/* {isOpen &&
                            <div>
                                {props.postItems.comments.map((curr, i) => (

                                    <Comment key={i} />

                                ))
                                }
                            </div>
                        } */}
                        </div>
                    </div>
                </div>
            </div>
        );
}



function Comment() {
    return (
        <div>
            <textarea name="" id="" />
        </div>
    )
}
