    //                 .then(res => {
    //                     console.log(res);
    //                     console.log(res.data);
    //                     Navigate('/Home');

    //                 })

    //         })
    // }

    const username = useRef();
    const nickname = useRef();
    const password = useRef();
    const passwordAgain = useRef();
    const history = useHistory();

    const mySubmit = async (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        if (passwordAgain.current.value !== password.current.value) {
            passwordAgain.current.setCustomValidity("Passwords don't match!");
        } else {
            const user = {
                nickname: nickname.current.value,
                username: username.current.value,
                password: password.current.value,
            };
            try {
                await axios.post("/auth/register", user);
                history.push("/login");
            } catch (err) {
                console.log(err);
            }
        }
    };
    return (
        <div className="login">
            <div className="loginWrapper">
                <div className="loginLeft">
                    <span className="loginDesc">
                        <h1> Glad to see you here</h1>
                        Join our 'Speak Your Heart Out' family

                    </span>
                </div>
                <div className="loginRight">
                    <div className="loggedBox">
                        <form className="loginBox" onSubmit={(e) => { mySubmit(e) }} >
                            <input ref={nickname} type="text" name='nickname' placeholder="Nick name" className="loginInput" />
                            <input ref={username} type="text" name='username' placeholder="Email" className="loginInput" />
                            <input ref={password} type="password" name='password' placeholder="Password" className="loginInput" />
                            <input ref={passwordAgain} type="password" name='Cpassword' placeholder="Confirm password" className="loginInput" />
                            <label className='validNick'></label>
                            <input className="loginButton" type="submit" value="Sign Up" id="Submit" />
                            <span> Already member?</span>
                            <span className='signLink'> <Link to={'/Login'}> log in</Link> </span>

                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}
