import axios from 'axios';
import React from 'react';
import { allInfoUser, getId } from '../configstore';

export function Profile() {
    // let url = 'http://127.0.0.1:5789/people/';
    // url = url + getId().id;
    // let user = {
    //     id: getId().id,
    //     nickname: "",
    //     username: "",
    //     password: "",
    // }
    // axios.get(url)
    //     .then(response => {
    //         user = response.data;
    //     });

    let user = allInfoUser()
    console.log(user);

    function handleUpdate(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        var formdata = new FormData(e.target as HTMLFormElement);
        let url = 'http://127.0.0.1:5789/people/';
        url = url + getId().id;
        const user = {
            id: getId().id,
            nickname: formdata.get('nickname'),
            username: formdata.get('username'),
            password: formdata.get('password'),
        }

        axios.put(url,
            user
        )
            .then(res => {
                console.log(res.data);
            })
    }





    return (
        <div>
            <div className='divAroundProfile'>
                <form onSubmit={(e) => { handleUpdate(e) }}   >
                    <div id="DivAroundFormElements">
                        <label>choose a nick name</label><br />
                        <div id="nickname">
                            <input type="text" id="nickname" name="nickname" placeholder="nick name" defaultValue={user.nickname} />
                        </div>
                        <label className='validNick'></label>
                        <br />
                        <label>Choose your user name</label><br />
                        <input type="text" id="username" name="username" placeholder="@gmail.com" defaultValue={user.username} />
                        <label className='validUser'></label>
                        <br />
                        <label >Create a password</label><br />
                        <input type="password" id="password" name="password" defaultValue={user.password} />
                        <br />
                        <input type="submit" value="Update" id="Submit" />

                    </div>
                </form>
            </div>
        </div>
    )

}
