import "../styles/Pages/share.css";
import { PermMedia, Label, Room, EmojiEmotions, Star } from "@material-ui/icons"
import { getId } from './../configstore';
import axios from "axios";
import { useEffect, useState } from "react";

export function Share(props: { category: string }) {
    let url = 'http://127.0.0.1:5789/people/';
    let url1 = 'http://127.0.0.1:5789/post/';
    const Id = getId().id;
    url = url + getId().id;
    url1 = url1 + getId().id;
    const [user, setuser] = useState({ nickname: "", posts: [] })
    const [desc, setDesc] = useState('')

    useEffect(() => {
        axios.get(url)
            .then(response => {
                console.log(response.data);

                setuser(response.data);
            });
    }, [])


    const post = {
        postId: user.posts.length + 1,
        userId: Id,
        content: desc,
        img: "",
        likes: [],
        Comment: [],
        category: props.category
    }


    function addPost() {


        axios.put(url1, {
            post
        })
            .then(response => {
                console.log(response.data);

            });
    }

    return (
        <div className="sharearoundAll">
            <div className="share">
                <div className="shareWrapper">
                    <div className="shareTop">
                        <img className="shareProfileImg" src="./images/anonymous.jpg" alt="" />
                        <input onChange={event => setDesc(event.target.value)}
                            placeholder={"Whats in your mind" + " " + user.nickname + "?"}
                            className="shareInput"

                        />
                    </div>
                    <hr className="shareHr" />
                    <div className="shareBottom">
                        <div className="shareOptions">
                            <div className="shareOption">
                                <PermMedia htmlColor="tomato" className="shareIcon" />
                                <span className="shareOptionText">Photo or Video</span>
                            </div>
                            <div className="shareOption">
                                <input type="checkbox" />
                                <span className="shareOptionText">Professional</span>
                            </div>
                            {/* <div className="shareOption">
                                <Star htmlColor="blue" className="shareIcon" />
                                <span className="shareOptionText">Professional</span>
                            </div> */}
                            <div className="shareOption">
                                <select name="" id="">
                                    <option value="select"> select a category : </option>
                                    <option value="select"> my : </option>
                                    <option value="select"> select a category : </option>
                                </select>
                                <span className="shareOptionText">Location</span>
                            </div>
                        </div>
                        <button onClick={addPost} className="shareButton">Share</button>
                    </div>
                </div>
            </div>
        </div>
    );
}
