import axios from 'axios';
import React from 'react';
import { allInfoUser, getId } from '../configstore';
import '../styles/Pages/profile.css';

export function Profile() {


    let user = allInfoUser()
    console.log(user);

    function handleUpdate(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        var formdata = new FormData(e.target as HTMLFormElement);
        let url = 'http://127.0.0.1:5789/people/';
        url = url + getId().id;
        const user = {
            id: getId().id,
            nickname: formdata.get('nickname'),
            username: formdata.get('username'),
            password: formdata.get('password'),
        }

        axios.put(url,
            user
        )
            .then(res => {
                console.log(res.data);
            })
    }

    return (
        <div>
            <div className='divAroundProfile'>
                <form onSubmit={(e) => { handleUpdate(e) }}   >
                    <div id="DivAroundFormElements">
                        <label>nick name :</label>
                        <div id="nickname">
                            <input type="text" id="nicknameProfile" name="nickname" placeholder="nick name" defaultValue={user.nickname} />
                        </div>
                        <label className='validNick'></label>

                        <label> user name:</label>
                        <input type="text" id="usernameProfile" name="username" placeholder="@gmail.com" defaultValue={user.username} />
                        <label className='validUser'></label>
                        <br />
                        <label >password:</label><br />
                        <input type="password" id="passwordProfile" name="password" defaultValue={user.password} />
                        <br />
                        <input type="submit" value="Update" id="Submit" />

                    </div>
                </form>
            </div>
        </div>
    )

}
