import { Outlet } from 'react-router-dom'
import '../styles/Pages/asking.css'
import { AskIcons, AskNav } from './../components/navbar';
import { askNavArr, AskNAvIcons, Posts } from './../utils/data';

import "./post.css";
import { MoreVert } from "@material-ui/icons";
import { Users } from "./../utils/data";
import { useState } from "react";




export function Asking() {

    return (

        <header className='askPage'>
            <div className='Ask'>
                <div className='divAroundNavs' >
                    <AskNav navItems={askNavArr} />
                    <AskIcons navItems={AskNAvIcons} />
                    {Posts.map((p) => (
                        <Post key={p.id} posts={Posts} />
                    ))}
                </div>
                <Outlet />

            </div>

        </header >

    )

}



export function Post(props: { posts: { id: string, desc: string, photo: string, date: string, userId: number, like: number, comment: number } }) {
    const [like, setLike] = useState(props.posts.like)
    const [isLiked, setIsLiked] = useState(false)

    const likeHandler = () => {
        setLike(isLiked ? like - 1 : like + 1)
        setIsLiked(!isLiked)
    }
    return (
        <div className="post">
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTopLeft">
                        <img
                            className="postProfileImg"
                            src={Users.filter((u) => u.id === props.posts?.userId)[0].profilePicture}
                            alt=""
                        />
                        <span className="postUsername">
                            {Users.filter((u) => u.id === props.posts?.userId)[0].username}
                        </span>
                        <span className="postDate">{props.posts.date}</span>
                    </div>
                    <div className="postTopRight">
                        <MoreVert />
                    </div>
                </div>
                <div className="postCenter">
                    <span className="postText">{props.posts?.desc}</span>
                    <img className="postImg" src={props.posts.photo} alt="" />
                </div>
                <div className="postBottom">
                    <div className="postBottomLeft">
                        <img className="likeIcon" src="assets/like.png" onClick={likeHandler} alt="" />
                        <img className="likeIcon" src="assets/heart.png" onClick={likeHandler} alt="" />
                        <span className="postLikeCounter">{like} people like it</span>
                    </div>
                    <div className="postBottomRight">
                        <span className="postCommentText">{props.posts.comment} comments</span>
                    </div>
                </div>
            </div>
        </div>
    );
}
