import { useEffect, useState } from "react"
import { MoreVert } from "@material-ui/icons";
import { Users } from "./../utils/data";
import '../styles/Pages/post.css'
import axios from "axios";
import { getId } from "../configstore";

export function Post(props: { postItems: { id: string, userId: string, desc: string, like: number, comments: number, date: string, photo: string } }) {

    const [like, setLike] = useState(props.postItems.like);
    const [isLiked, setIsLiked] = useState(false);

    let url = 'http://127.0.0.1:5789/people/';

    const Id = getId().id;
    url = url + getId().id;
    const [user, setuser] = useState('')

    useEffect(() => {
        axios.get(url)
            .then(response => {
                console.log(response.data);

                setuser(response.data.nickname);
            });
    }, [])


    const likeHandler = () => {
        setLike(isLiked ? like - 1 : like + 1)
        setIsLiked(!isLiked)
    }

    return (
        <div className="aroundAll">
            <div className="post">
                <div className="postWrapper">
                    <div className="postTop">
                        <div className="postTopLeft">
                            <img
                                className="postProfileImg"
                                src="C:\Users\USER\Desktop\final project\my final perfect pro\myapp\public\images\anonymous.jpg"
                                alt=""
                            />
                            <span className="postUsername">
                                {Users.filter((u) => u.id === props.postItems?.userId)[0].username}
                            </span>
                            <span className="postDate">{props.postItems?.date}</span>
                        </div>
                        <div className="postTopRight">
                            <MoreVert />
                        </div>
                    </div>
                    <div className="postCenter">
                        <img className="postImg" src={props.postItems.photo} />

                        <p> {props.postItems.desc} </p>

                    </div>
                    <div className="postBottom">
                        <div className="postBottomLeft">
                            <img className="likeIcon" src="./images/like.png" onClick={likeHandler} alt="" />
                            <img className="likeIcon" src="./images/heart.png" onClick={likeHandler} alt="" />
                            <span className="postLikeCounter">{like} people like it</span>
                        </div>
                        <div className="postBottomRight">
                            <span className="postCommentText">{props.postItems.comments} comments</span>
                        </div>
                    </div>
                </div>

            </div>
        </div>
    );
}
