import axios from 'axios';
import { useEffect } from 'react';
import '../styles/Components/signup.css';

/* Function that returns an HTML form for signing up to web */
export function Signup() {
    const url = 'http://127.0.0.1:5789/user';
    useEffect(() => {
        axios.get(url)
            .then(response => {
                console.log(response.data);

            });
    }, []);
    function mySubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault()
        console.log("hello");
        var formData = new FormData(e.target as HTMLFormElement);
        console.log(formData.get("fname"));
        console.log(formData.get("username"));

        axios.post(url, {
            formData,
        })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
    }

    return (
        <div className='DivAroundSignUpForm'>
            <form onSubmit={(e) => { mySubmit(e) }} >
                <div id="DivAroundFormElements">
                    <label>choose a nick name</label><br />
                    <div id="nickname">
                        <input type="text" id="nickname" name="nickname" placeholder="nick name" />
                    </div>
                    <br />
                    <label>Choose your user name</label><br />
                    <input type="text" id="username" name="username" placeholder="@gmail.com" />
                    <br />
                    <label >Create a password</label><br />
                    <input type="password" id="password" name="password" />
                    <br />
                    <label >Confirm your password</label><br />
                    <input type="password" id="conpassword" name="conpassword" />
                    <br />
                    <label >Birthday</label><br />
                    <div id="selectbirthday">
                        <select name="month" id="month" >
                            <option value="none">Month</option>
                            <option value="1">JAN</option>
                            <option value="2">FEB</option>
                            <option value="3">MAR</option>
                            <option value="4">APR</option>
                            <option value="5">MAY</option>
                            <option value="6">JUN</option>
                            <option value="7">JUL</option>
                            <option value="8">AUG</option>
                            <option value="9">SEP</option>
                            <option value="10">OCT</option>
                            <option value="11">NOV</option>
                            <option value="11">DEC</option>
                        </select>
                        <input type="number" name="Day" min="1" max="31" step="1" id="Day" disabled placeholder="Day" />
                        <input type="number" min="1919" max="2021" step="1" id="Year" name="Year" disabled placeholder="Year" />
                    </div>
                    <br />

                    <input type="submit" value="Submit" id="Submit" />
                    <input type={'Button'} value={"X"} />
                </div>
            </form>
        </div>
    )

}
