import { Outlet } from 'react-router-dom'
import '../styles/Pages/asking.css'
import { AskIcons, AskNav } from './../components/navbar';
import { askNavArr, AskNAvIcons } from './../utils/data';

import "./post.css";
import { MoreVert } from "@material-ui/icons";
import { Users } from "./../utils/data";
import { useState } from "react";



export function Asking() {

    return (

        <header className='askPage'>
            <div className='Ask'>
                <div className='divAroundNavs' >
                    <AskNav navItems={askNavArr} />
                    <AskIcons navItems={AskNAvIcons} />
                </div>
                <Outlet />

            </div>

        </header >

    )

}



export function Post() {
    const [like, setLike] = useState(post.like)
    const [isLiked, setIsLiked] = useState(false)

    const likeHandler = () => {
        setLike(isLiked ? like - 1 : like + 1)
        setIsLiked(!isLiked)
    }
    return (
        <div className="post">
            <div className="postWrapper">
                <div className="postTop">
                    <div className="postTopLeft">
                        <img
                            className="postProfileImg"
                            src={Users.filter((u) => u.id === post?.userId)[0].profilePicture}
                            alt=""
                        />
                        <span className="postUsername">
                            {Users.filter((u) => u.id === post?.userId)[0].username}
                        </span>
                        <span className="postDate">{post.date}</span>
                    </div>
                    <div className="postTopRight">
                        <MoreVert />
                    </div>
                </div>
                <div className="postCenter">
                    <span className="postText">{post?.desc}</span>
                    <img className="postImg" src={post.photo} alt="" />
                </div>
                <div className="postBottom">
                    <div className="postBottomLeft">
                        <img className="likeIcon" src="assets/like.png" onClick={likeHandler} alt="" />
                        <img className="likeIcon" src="assets/heart.png" onClick={likeHandler} alt="" />
                        <span className="postLikeCounter">{like} people like it</span>
                    </div>
                    <div className="postBottomRight">
                        <span className="postCommentText">{post.comment} comments</span>
                    </div>
                </div>
            </div>
        </div>
    );
}
