import React from 'react';
import { useAlertModal, useDialogModal } from './src';
import Button from '@material-ui/core/Button';
import './app.css';


function App() {
    const showAlertModal = useAlertModal();
    const showDialogModal = useDialogModal();


    const handleAlertModalClick = (severity, title, message) => {
        showAlertModal({
            severity,
            title,
            message
        })
    }

    const handleDialogModalClick = () => {
        showDialogModal({
            title: 'Dialog Modal',
            hideActions: true,
            render: () => {
                return (
                    <p style={{ padding: '20px' }}>This is the content</p>
                )
            }
        })
    }

    return (
        <div className="container" onClick={e => e.stopPropagation()}>
            <Button variant="contained" color="primary" onClick={() => handleAlertModalClick('info', 'Info', 'show info message')}>Show info Message</Button>
            <Button variant="contained" color="secondary" onClick={() => handleAlertModalClick('warning', 'Warning', 'show warning message')}>Show warning Message</Button>
            <Button variant="contained" color="primary" onClick={() => handleAlertModalClick('success', 'Success', 'show success message')}>Show success Message</Button>
            <Button variant="contained" color="secondary" onClick={() => handleAlertModalClick('error', 'Error', 'show error message')}>Show error Message</Button>

            <Button variant="contained" color="secondary" onClick={() => handleDialogModalClick('error', 'Error', 'show error message')}>Show error Message</Button>
        </div>
    )
}

export default App;